        e.preventDefault();
        this.e.value(this.state)
    }

    render(){
        return(
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input type="text" onChange={this.handleUsername} placeholder="שם משתמש" className="username"/>
                    <br/>
