            {
                name: 'משה',
                phoneNumber: '052-8090399',
                adress: 'הר הצופים 29 ',
                city: 'חולון',
                id: 1
            },

            {
                name: 'ג׳ניפר',
                phoneNumber: '052-1234567',
                adress: 'שקר כל שהוא ',
                city: 'חולון',
                id: 2
            },

            {
                name: 'דיאנה',
                phoneNumber: '052-1234567',
                adress: 'שקר כל שהוא ',
                city: 'חולון',
                id: 3
            },


        ]
    }

