                <input  className="NewClientInput" type="text" placeholder="מספר טלפון " />
                <input className="NewClientInput" type="text" placeholder="כתובת " />
                <input  className="NewClientInput" type="text" placeholder="דואר אלקטרוני " />
                <button className="SaveNewClient">שמור</button>
                <button className="SaveNewClient">חזור לתפריט הראשי</button>
            </form>
        )
    }
}


export default AddNewClient;
